import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const Home = ({ user }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    axiosInstance
      .get("/jobs")
      .then((res) => setJobs(res.data))
      .catch((err) => console.log(err));
  }, []);

  const applyJob = (jobId) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("You must be logged in to apply for a job!");
      return;
    }

    axiosInstance
      .post(`/jobs/apply/${jobId}`)
      .then((res) => {
        alert(`${user?.email} applied for ${jobs.find(j => j._id === jobId)?.title}`);
      })
      .catch((err) =>
        alert(err.response?.data?.msg || "Error applying")
      );
  };

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {jobs.map((job) => (
        <div key={job._id} className="border p-4 rounded shadow">
          <h2 className="font-bold">{job.title}</h2>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>
            Location: {job.location} | Salary: {job.salary || "N/A"}
          </p>
          <button
            className="bg-green-500 text-white px-4 py-2 mt-2 rounded"
            onClick={() => applyJob(job._id)}
          >
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
