import React from "react";
import { Link } from "react-router-dom";

const Navbar = ({ user, setUser }) => {
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <nav style={{ padding: "10px", background: "#4285f4", color: "#fff", display: "flex", justifyContent: "space-between" }}>
      <div><Link to="/" style={{ color: "#fff", textDecoration: "none" }}>Home</Link></div>
      <div>
        {user ? (
          <>
            Hello, {user.name} | <span style={{ cursor: "pointer" }} onClick={handleLogout}>Logout</span>
          </>
        ) : (
          <>
            <Link to="/login" style={{ color: "#fff", marginRight: "10px" }}>Login</Link>
            <Link to="/register" style={{ color: "#fff" }}>Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
