import React from "react";
import { Link } from "react-router-dom";

export default function JobCard({ job }) {
  return (
    <div className="bg-white shadow-md p-4 rounded w-full border hover:shadow-lg transition">
      <h2 className="font-bold text-xl mb-1">{job.title}</h2>

      <p className="text-gray-700 font-medium">{job.company}</p>

      <p className="text-gray-600 mt-2 mb-2">
        {job.description?.length > 60
          ? job.description.substring(0, 60) + "..."
          : job.description}
      </p>

      <p className="text-gray-700 mb-4">
        <strong>Location:</strong> {job.location} <br />
        <strong>Salary:</strong> {job.salary || "Not Provided"}
      </p>

      <Link
        to={`/job/${job._id}`}
        className="bg-blue-600 text-white block text-center py-2 rounded hover:bg-blue-700 transition"
      >
        View Details
      </Link>
    </div>
  );
}
