import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem("token");
  const role = localStorage.getItem("role");

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/login");
  };

  return (
    <nav className="bg-blue-600 p-4 text-white flex justify-between">
      <div className="text-xl font-bold">
        <Link to="/">JobBoard</Link>
      </div>
      <div className="space-x-4">
        <Link to="/">Home</Link>
        {isLoggedIn && role === "candidate" && <Link to="/candidate">Profile</Link>}
        {isLoggedIn && role === "employer" && <Link to="/employer">Dashboard</Link>}
        {!isLoggedIn ? (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        ) : (
          <button onClick={logout} className="bg-red-500 px-2 py-1 rounded">Logout</button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
