// src/pages/JobDetail.js
import React, { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance";

const JobDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [alreadyApplied, setAlreadyApplied] = useState(false);

  const [resume, setResume] = useState(null);
  const [coverLetter, setCoverLetter] = useState("");

  // Load user
  const user = JSON.parse(localStorage.getItem("user") || "null");
  const token = localStorage.getItem("token");

  const role = user?.role || null;
  const userId = user?.id || null;

  // Fetch job details + check if applied
  useEffect(() => {
    const loadJob = async () => {
      try {
        const res = await axiosInstance.get(`/jobs/${id}`);
        setJob(res.data);

        // Check if applied using Application model
        const appliedCheck = await axiosInstance.get("/jobs/my-applications");

        const hasApplied = appliedCheck.data.some(
          (app) => app.job?._id === id
        );

        setAlreadyApplied(hasApplied);
      } catch (err) {
        console.error(err);
      }

      setLoading(false);
    };

    loadJob();
  }, [id]);

  // APPLY FUNCTION
  const applyJob = async () => {
    if (!token) return navigate("/login");

    if (role === "employer") {
      alert("Employers cannot apply.");
      return;
    }

    if (!resume) {
      alert("Please upload your resume (PDF/DOC).");
      return;
    }

    const formData = new FormData();
    formData.append("resume", resume);
    formData.append("coverLetter", coverLetter);

    try {
      const res = await axiosInstance.post(`/jobs/apply/${id}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert(res.data.msg);
      setAlreadyApplied(true);
    } catch (err) {
      alert(err.response?.data?.msg || "Error applying");
    }
  };

  if (loading)
    return <p className="text-center mt-10">Loading job details...</p>;

  if (!job)
    return (
      <p className="text-center mt-10 text-red-500">Job not found.</p>
    );

  return (
    <div className="max-w-4xl mx-auto my-8 p-6 bg-white rounded shadow">
      <Link to="/" className="text-blue-600 underline mb-4 inline-block">
        ‚Üê Back to Jobs
      </Link>

      <h1 className="text-3xl font-bold mb-4">{job.title}</h1>

      <p className="text-gray-700 mb-1"><strong>Company:</strong> {job.company}</p>
      <p className="text-gray-700 mb-1"><strong>Location:</strong> {job.location}</p>
      <p className="text-gray-700 mb-3"><strong>Salary:</strong> {job.salary || "N/A"}</p>
      <p className="text-gray-700 whitespace-pre-line mb-6 leading-relaxed">{job.description}</p>

      {/* Employer restriction */}
      {role === "employer" && (
        <p className="text-red-600 font-semibold">Employers cannot apply for jobs.</p>
      )}

      {/* Already applied */}
      {role === "candidate" && alreadyApplied && (
        <button disabled className="bg-gray-400 text-white px-6 py-3 rounded cursor-not-allowed">
          Already Applied
        </button>
      )}

      {/* Application form */}
      {role === "candidate" && !alreadyApplied && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-4">Apply for this Job</h2>

          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setResume(e.target.files[0])}
            className="w-full mb-4 border rounded p-2"
          />

          <textarea
            placeholder="Cover Letter (optional)"
            value={coverLetter}
            onChange={(e) => setCoverLetter(e.target.value)}
            className="w-full border rounded p-3 mb-4"
            rows="4"
          />

          <button
            onClick={applyJob}
            className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-500 transition"
          >
            Submit Application
          </button>
        </div>
      )}

      {!token && (
        <button
          onClick={() => navigate("/login")}
          className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-500 transition"
        >
          Login to Apply
        </button>
      )}
    </div>
  );
};

export default JobDetail;
