// src/pages/Home.js
import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    axiosInstance.get("/jobs")
      .then(res => {
        setJobs(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching jobs:", err);
        setError("Failed to fetch jobs");
        setLoading(false);
      });
  }, []);

  const applyJob = (jobId) => {
    // For now ignore auth
    axiosInstance.post(`/jobs/apply/${jobId}`)
      .then(res => alert(res.data.msg))
      .catch(err => alert(err.response?.data?.msg || "Error applying"));
  };

  if (loading) return <div className="p-4">Loading jobs...</div>;
  if (error) return <div className="p-4 text-red-500">{error}</div>;

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {jobs.map(job => (
        <div key={job._id} className="border p-4 rounded shadow">
          <h2 className="font-bold">{job.title}</h2>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary || "N/A"}</p>
          <button
            className="bg-green-500 text-white px-4 py-2 mt-2 rounded"
            onClick={() => applyJob(job._id)}
          >
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
