import { Link } from "react-router-dom";

export default function Navbar({ user, logout }) {
  return (
    <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
      <div className="font-bold text-lg">
        <Link to="/">Job Board</Link>
      </div>
      <div>
        {!user ? (
          <>
            <Link to="/login" className="px-3 hover:underline">Login</Link>
            <Link to="/register" className="px-3 hover:underline">Register</Link>
          </>
        ) : (
          <>
            <span className="px-3">Hello, {user.name}</span>
            {user.role === "employer" && (
              <Link to="/employer-dashboard" className="px-3 hover:underline">Dashboard</Link>
            )}
            {user.role === "candidate" && (
              <Link to="/candidate-dashboard" className="px-3 hover:underline">My Applications</Link>
            )}
            <button onClick={logout} className="px-3 py-1 bg-white text-blue-600 rounded hover:bg-gray-200">
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
