// src/pages/Profile/UploadPicture.js
import React, { useState } from "react";
import axiosInstance from "../../utils/axiosInstance";
import { useNavigate } from "react-router-dom";

export default function UploadPicture() {
  const [file, setFile] = useState(null);
  const navigate = useNavigate();

  const upload = async () => {
    if (!file) {
      alert("Please select an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("profilePic", file);   // MUST MATCH BACKEND

    try {
      const res = await axiosInstance.put("/profile/upload-picture", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Profile picture updated successfully!");
      navigate("/profile");
    } catch (err) {
      console.log(err);
      alert(err.response?.data?.msg || "Upload failed");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white p-6 shadow rounded">
      <h1 className="text-2xl font-bold mb-4">Update Profile Picture</h1>

      <input
        type="file"
        accept="image/*"
        onChange={(e) => setFile(e.target.files[0])}
        className="border p-3 w-full mb-4"
      />

      <button
        onClick={upload}
        className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 transition"
      >
        Upload
      </button>
    </div>
  );
}
