import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

function Home() {
  const [jobs, setJobs] = useState([]);
  const [user, setUser] = useState(null); // check if logged in

  useEffect(() => {
    fetchJobs();
    const loggedInUser = JSON.parse(localStorage.getItem("user"));
    setUser(loggedInUser);
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/jobs");
      setJobs(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  const applyJob = async (jobId) => {
    if (!user) {
      toast.error("You must be logged in to apply");
      return;
    }

    try {
      await axios.post(`http://localhost:5000/api/jobs/apply/${jobId}`, {
        userId: user._id,
      });
      toast.success("Application submitted!");
    } catch (err) {
      console.log(err);
      toast.error(err.response?.data?.msg || "Error applying job");
    }
  };

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {jobs.length === 0 && <p>No jobs available</p>}
      {jobs.map((job) => (
        <div key={job._id} className="border p-4 rounded shadow">
          <h2 className="font-bold text-lg">{job.title}</h2>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary || "N/A"}</p>
          <button
            onClick={() => applyJob(job._id)}
            className="mt-2 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
}

export default Home;
