// src/pages/Profile/EditProfile.js
import React, { useEffect, useState } from "react";
import axiosInstance from "../../utils/axiosInstance";
import { useNavigate } from "react-router-dom";

const EditProfile = () => {
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [profilePic, setProfilePic] = useState(null);
  const [resume, setResume] = useState(null);
  const [companyLogo, setCompanyLogo] = useState(null);

  useEffect(() => {
    axiosInstance
      .get("/profile")
      .then((res) => setProfile(res.data))
      .catch((err) => console.log("Profile Load Error:", err));
  }, []);

  // MAIN SAVE FUNCTION
  const handleSave = async () => {
    try {
      // ==========================
      // 1️⃣ SAVE ALL TEXT FIELDS
      // ==========================
      const payload = {
        name: profile.name,
        email: profile.email,
        bio: profile.bio || "",
        phone: profile.phone || "",
        experience: profile.experience || "",
        skills: JSON.stringify(profile.skills || []),
        companyName: profile.companyName || "",
        companyWebsite: profile.companyWebsite || "",
        companyDescription: profile.companyDescription || "",
      };

      await axiosInstance.put("/profile", payload);

      // ===============================
      // 2️⃣ UPLOAD PROFILE PICTURE
      // ===============================
      if (profilePic) {
        const fd = new FormData();
        fd.append("profilePic", profilePic);

        await axiosInstance.post("/profile/upload-profile-pic", fd, {
          headers: { "Content-Type": "multipart/form-data" },
        });
      }

      // ===============================
      // 3️⃣ UPLOAD RESUME (Candidate)
      // ===============================
      if (resume && profile.role === "candidate") {
        const fd = new FormData();
        fd.append("resume", resume);

        await axiosInstance.post("/profile/upload-resume", fd, {
          headers: { "Content-Type": "multipart/form-data" },
        });
      }

      // ==================================
      // 4️⃣ UPLOAD COMPANY LOGO (Employer)
      // ==================================
      if (companyLogo && profile.role === "employer") {
        const fd = new FormData();
        fd.append("companyLogo", companyLogo);

        await axiosInstance.post("/profile/upload-logo", fd, {
          headers: { "Content-Type": "multipart/form-data" },
        });
      }

      alert("Profile updated successfully!");
      navigate("/profile");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.msg || "Error updating profile");
    }
  };

  if (!profile) return <p className="text-center mt-10">Loading...</p>;

  return (
    <div className="max-w-3xl mx-auto mt-10 bg-white p-6 rounded shadow">
      <h1 className="text-2xl font-bold mb-5">Edit Profile</h1>

      {/* NAME */}
      <input
        type="text"
        value={profile.name}
        onChange={(e) => setProfile({ ...profile, name: e.target.value })}
        className="w-full border p-2 mb-4"
        placeholder="Name"
      />

      {/* EMAIL */}
      <input
        type="email"
        value={profile.email}
        onChange={(e) => setProfile({ ...profile, email: e.target.value })}
        className="w-full border p-2 mb-4"
        placeholder="Email"
      />

      {/* BIO */}
      <textarea
        value={profile.bio || ""}
        onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
        className="w-full border p-2 mb-4"
        placeholder="Short Bio"
        rows={3}
      />

      {/* PROFILE PICTURE */}
      <h3 className="font-semibold mt-4 mb-2">Profile Picture</h3>
      <input
        type="file"
        accept="image/*"
        onChange={(e) => setProfilePic(e.target.files[0])}
        className="mb-4"
      />

      {/* CANDIDATE FIELDS */}
      {profile.role === "candidate" && (
        <>
          <input
            type="text"
            value={profile.phone || ""}
            onChange={(e) => setProfile({ ...profile, phone: e.target.value })}
            className="w-full border p-2 mb-4"
            placeholder="Phone"
          />

          <textarea
            value={profile.experience || ""}
            onChange={(e) =>
              setProfile({ ...profile, experience: e.target.value })
            }
            className="w-full border p-2 mb-4"
            placeholder="Experience"
            rows={3}
          />

          <input
            type="text"
            value={(profile.skills || []).join(", ")}
            onChange={(e) =>
              setProfile({
                ...profile,
                skills: e.target.value.split(",").map((x) => x.trim()),
              })
            }
            className="w-full border p-2 mb-4"
            placeholder="Skills (comma separated)"
          />

          {/* RESUME */}
          <h3 className="font-semibold mt-4 mb-2">Upload Resume</h3>
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setResume(e.target.files[0])}
            className="mb-4"
          />
        </>
      )}

      {/* EMPLOYER FIELDS */}
      {profile.role === "employer" && (
        <>
          <input
            type="text"
            value={profile.companyName || ""}
            onChange={(e) =>
              setProfile({ ...profile, companyName: e.target.value })
            }
            className="w-full border p-2 mb-4"
            placeholder="Company Name"
          />

          <input
            type="text"
            value={profile.companyWebsite || ""}
            onChange={(e) =>
              setProfile({ ...profile, companyWebsite: e.target.value })
            }
            className="w-full border p-2 mb-4"
            placeholder="Company Website"
          />

          <textarea
            value={profile.companyDescription || ""}
            onChange={(e) =>
              setProfile({ ...profile, companyDescription: e.target.value })
            }
            className="w-full border p-2 mb-4"
            placeholder="Company Description"
            rows={3}
          />

          <h3 className="font-semibold mt-4 mb-2">Upload Company Logo</h3>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setCompanyLogo(e.target.files[0])}
            className="mb-4"
          />
        </>
      )}

      <button
        onClick={handleSave}
        className="mt-5 bg-blue-600 text-white px-4 py-2 rounded"
      >
        Save Changes
      </button>
    </div>
  );
};

export default EditProfile;
