import React, { useEffect, useState } from "react";
import axiosInstance from "../axiosInstance";

const Home = ({ user }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    axiosInstance.get("/jobs").then(res => setJobs(res.data));
  }, []);

  const applyJob = async (jobId) => {
    if (!user) {
      alert("Please login first");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const res = await axiosInstance.post(`/jobs/apply/${jobId}`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(`${user.name} applied for ${jobs.find(j => j._id === jobId).title}`);
    } catch (error) {
      alert(error.response?.data?.msg || "Error applying for job");
    }
  };

  return (
    <div style={{ display: "flex", gap: "20px", padding: "20px" }}>
      {jobs.map(job => (
        <div key={job._id} style={{ border: "1px solid #ccc", padding: "20px", width: "300px" }}>
          <h3>{job.title}</h3>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary}</p>
          <button onClick={() => applyJob(job._id)} style={{ background: "green", color: "#fff", padding: "10px", border: "none", cursor: "pointer" }}>
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
