import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

export default function Home({ user }) {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const res = await axiosInstance.get("/jobs");
      setJobs(res.data);
    };
    fetchJobs();
  }, []);

  const handleApply = (job) => {
    if (!user) return alert("Please login first!");
    alert(`${user.name} applied for ${job.title}`);
  };

  return (
    <div className="p-6 flex flex-wrap justify-center">
      {jobs.map((job) => (
        <div
          key={job._id}
          className="bg-white shadow-lg rounded-md p-6 m-4 w-80 hover:shadow-xl transition"
        >
          <h2 className="font-bold text-xl mb-2">{job.title}</h2>
          <p className="text-gray-700">{job.company}</p>
          <p className="text-gray-600">{job.description}</p>
          <p className="text-gray-500">
            Location: {job.location} | Salary: {job.salary}
          </p>
          <button
            onClick={() => handleApply(job)}
            className="mt-4 w-full bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded transition"
          >
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
}
