import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const CandidateDashboard = () => {
  const [appliedJobs, setAppliedJobs] = useState([]);

  useEffect(() => {
    const userId = JSON.parse(atob(localStorage.getItem("token"))).id;
    axiosInstance.get(`/jobs/applied/${userId}`)
      .then(res => setAppliedJobs(res.data))
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">My Applied Jobs</h1>
      <ul className="space-y-2">
        {appliedJobs.length ? appliedJobs.map(jobId => (
          <li key={jobId} className="border p-2 rounded shadow">{jobId}</li>
        )) : <p>You havenâ€™t applied to any jobs yet.</p>}
      </ul>
    </div>
  );
};

export default CandidateDashboard;
