import React from "react";
import { Link } from "react-router-dom";

const Navbar = ({ user, onLogout }) => {
  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
      <div>
        <Link to="/" className="font-bold text-lg">Job Board</Link>
      </div>
      <div className="space-x-4">
        {user ? (
          <>
            <span>Hello, {user.name}</span>
            <button onClick={onLogout} className="bg-red-500 px-2 py-1 rounded">Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" className="bg-green-500 px-2 py-1 rounded">Login</Link>
            <Link to="/register" className="bg-blue-500 px-2 py-1 rounded">Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
