import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";
import Hero from "../components/Hero";
import JobCard from "../components/JobCard";
import JobSearch from "../components/JobSearch";
import Pagination from "../components/Pagination";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ðŸ”¥ NEW filters state (matches JobSearch.js)
  const [filters, setFilters] = useState({
    search: "",
    location: "",
    jobType: "",
    experience: "",
  });

  const [currentPage, setCurrentPage] = useState(1);
  const JOBS_PER_PAGE = 6;

  // Fetch jobs
  useEffect(() => {
    axiosInstance
      .get("/jobs")
      .then((res) => {
        setJobs(res.data);
        setFilteredJobs(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError("Unable to load jobs.");
        setLoading(false);
      });
  }, []);

  // Apply filters
  useEffect(() => {
    let results = [...jobs];

    if (filters.search) {
      results = results.filter((job) =>
        job.title.toLowerCase().includes(filters.search.toLowerCase()) ||
        job.company.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    if (filters.location) {
      results = results.filter((job) =>
        job.location.toLowerCase().includes(filters.location.toLowerCase())
      );
    }

    if (filters.jobType) {
      results = results.filter((job) => job.jobType === filters.jobType);
    }

    if (filters.experience) {
      results = results.filter(
        (job) => job.experienceLevel === filters.experience
      );
    }

    setFilteredJobs(results);
    setCurrentPage(1);
  }, [filters, jobs]);

  // Pagination
  const indexOfLastJob = currentPage * JOBS_PER_PAGE;
  const indexOfFirstJob = indexOfLastJob - JOBS_PER_PAGE;
  const currentJobs = filteredJobs.slice(indexOfFirstJob, indexOfLastJob);

  const totalPages = Math.ceil(filteredJobs.length / JOBS_PER_PAGE);

  // UI
  if (loading) return <p className="text-center mt-10">Loading...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;

  return (
    <div className="p-4 bg-gray-100 min-h-screen">
      
      {/* ðŸ”¥ Hero needs only search from filters */}
      <Hero
        search={filters.search}
        setSearch={(value) =>
          setFilters((prev) => ({ ...prev, search: value }))
        }
      />

      {/* ðŸ”¥ PASS CORRECT PROPS */}
      <div className="max-w-6xl mx-auto my-6">
        <JobSearch filters={filters} setFilters={setFilters} />
      </div>

      <div className="max-w-6xl mx-auto mb-4 text-gray-700 font-medium">
        {filteredJobs.length} Jobs Found
      </div>

      <div id="jobs-section" className="max-w-6xl mx-auto">
        {currentJobs.length === 0 ? (
          <p className="text-center text-gray-600 mt-10">
            No jobs match your search.
          </p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {currentJobs.map((job) => (
              <JobCard key={job._id} job={job} />
            ))}
          </div>
        )}

        {totalPages > 1 && (
          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            setPage={setCurrentPage}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
