import React from "react";

export default function Pagination({ currentPage, totalPages, setPage }) {
  if (totalPages <= 1) return null;

  const goToPage = (page) => {
    if (page < 1 || page > totalPages) return;
    setPage(page);

    // scroll to top of job list
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  const generatePages = () => {
    const pages = [];

    // Always show first page
    if (currentPage > 2) {
      pages.push(1);
    }

    // Add ellipsis before current range
    if (currentPage > 3) {
      pages.push("...");
    }

    // Add current, previous, next
    for (let i = currentPage - 1; i <= currentPage + 1; i++) {
      if (i >= 1 && i <= totalPages) {
        pages.push(i);
      }
    }

    // Add ellipsis after current range
    if (currentPage < totalPages - 2) {
      pages.push("...");
    }

    // Always show last page
    if (currentPage < totalPages - 1) {
      pages.push(totalPages);
    }

    return pages;
  };

  const pages = generatePages();

  return (
    <div className="flex justify-center items-center mt-6 space-x-2">

      {/* Prev Button */}
      <button
        onClick={() => goToPage(currentPage - 1)}
        disabled={currentPage === 1}
        className={`px-3 py-1 rounded ${
          currentPage === 1 ? "bg-gray-300 cursor-not-allowed" : "bg-gray-200 hover:bg-gray-300"
        }`}
      >
        Prev
      </button>

      {/* Page Numbers */}
      {pages.map((page, idx) => (
        <button
          key={idx}
          disabled={page === "..."}
          onClick={() => page !== "..." && goToPage(page)}
          className={`px-3 py-1 rounded ${
            page === currentPage
              ? "bg-blue-500 text-white"
              : page === "..."
              ? "bg-transparent cursor-default"
              : "bg-gray-200 hover:bg-gray-300"
          }`}
        >
          {page}
        </button>
      ))}

      {/* Next Button */}
      <button
        onClick={() => goToPage(currentPage + 1)}
        disabled={currentPage === totalPages}
        className={`px-3 py-1 rounded ${
          currentPage === totalPages
            ? "bg-gray-300 cursor-not-allowed"
            : "bg-gray-200 hover:bg-gray-300"
        }`}
      >
        Next
      </button>
    </div>
  );
}
