import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const Home = () => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const res = await axiosInstance.get("/jobs");
      setJobs(res.data);
    };
    fetchJobs();
  }, []);

  const handleApply = async (jobId, jobTitle) => {
    const token = localStorage.getItem("token");
    if (!token) return alert("Please login first");

    const name = localStorage.getItem("name"); // show name

    try {
      await axiosInstance.post(`/jobs/apply/${jobId}`);
      alert(`${name} applied for ${jobTitle}`);
    } catch (err) {
      alert(err.response?.data?.msg || "Error applying for job");
    }
  };

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-2 gap-4">
      {jobs.map((job) => (
        <div key={job._id} className="border p-4 rounded shadow">
          <h2 className="font-bold">{job.title}</h2>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary}</p>
          <button
            onClick={() => handleApply(job._id, job.title)}
            className="bg-green-500 text-white p-2 mt-2 rounded"
          >
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
