import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const EmployerDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({
    title: "",
    company: "",
    description: "",
    location: "",
    salary: "",
  });

  const userId = localStorage.getItem("userId");

  useEffect(() => {
    // Fetch jobs posted by this employer
    axiosInstance
      .get("/jobs")
      .then((res) => {
        const myJobs = res.data.filter((job) => job.postedBy === userId);
        setJobs(myJobs);
      })
      .catch((err) => console.log(err));
  }, [userId]);

  const handleInput = (e) => {
    setNewJob({ ...newJob, [e.target.name]: e.target.value });
  };

  const createJob = () => {
    axiosInstance
      .post("/jobs/create", newJob)
      .then((res) => {
        alert(res.data.msg);
        setJobs([...jobs, res.data.job]);
        setNewJob({ title: "", company: "", description: "", location: "", salary: "" });
      })
      .catch((err) => alert(err.response?.data?.msg || "Error creating job"));
  };

  const viewApplicants = (jobId) => {
    axiosInstance
      .get(`/jobs/applicants/${jobId}`)
      .then((res) => {
        const names = res.data.applicants.map((a) => a.name).join(", ");
        alert(`Applicants for this job: ${names || "None"}`);
      })
      .catch((err) => alert(err.response?.data?.msg || "Error fetching applicants"));
  };

  return (
    <div className="max-w-6xl mx-auto my-8 p-6">
      <h1 className="text-3xl font-bold mb-6">Employer Dashboard</h1>

      {/* New Job Form */}
      <div className="border rounded-lg p-4 shadow mb-8 bg-white">
        <h2 className="text-xl font-bold mb-4">Post a New Job</h2>
        <input
          type="text"
          name="title"
          placeholder="Job Title"
          value={newJob.title}
          onChange={handleInput}
          className="w-full p-2 mb-2 border rounded"
        />
        <input
          type="text"
          name="company"
          placeholder="Company Name"
          value={newJob.company}
          onChange={handleInput}
          className="w-full p-2 mb-2 border rounded"
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newJob.location}
          onChange={handleInput}
          className="w-full p-2 mb-2 border rounded"
        />
        <input
          type="text"
          name="salary"
          placeholder="Salary"
          value={newJob.salary}
          onChange={handleInput}
          className="w-full p-2 mb-2 border rounded"
        />
        <textarea
          name="description"
          placeholder="Job Description"
          value={newJob.description}
          onChange={handleInput}
          className="w-full p-2 mb-2 border rounded"
        />
        <button
          onClick={createJob}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-500 transition"
        >
          Post Job
        </button>
      </div>

      {/* Jobs List */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {jobs.length === 0 && <p className="text-gray-600">You haven't posted any jobs yet.</p>}
        {jobs.map((job) => (
          <div key={job._id} className="border rounded-lg p-4 shadow hover:shadow-lg transition bg-white">
            <h2 className="text-xl font-bold mb-2">{job.title}</h2>
            <p className="text-gray-700 mb-1"><strong>Company:</strong> {job.company}</p>
            <p className="text-gray-700 mb-1"><strong>Location:</strong> {job.location}</p>
            <p className="text-gray-700 mb-1"><strong>Salary:</strong> {job.salary || "N/A"}</p>
            <p className="text-gray-700 mb-3">{job.description}</p>
            <button
              onClick={() => viewApplicants(job._id)}
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-400 transition"
            >
              View Applicants
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EmployerDashboard;
