import React from "react";

function JobCard({ job, user }) {

  const handleApply = () => {
    if (!user) {
      alert("Please login to apply for this job");
      return;
    }
    
    // Make API call to backend to save application
    fetch("http://localhost:5000/api/apply", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${localStorage.getItem("token")}`
      },
      body: JSON.stringify({ jobId: job.id })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          alert("You have successfully applied for this job!");
        } else {
          alert("Something went wrong, try again.");
        }
      })
      .catch(err => {
        console.error(err);
        alert("Server error, try again later.");
      });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="font-bold mb-2">{job.title}</h2>
      <p>{job.company}</p>
      <p>{job.description}</p>
      <p>Location: {job.location} | Salary: {job.salary}</p>
      <button
        onClick={handleApply}
        className="mt-4 w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
      >
        Apply Job
      </button>
    </div>
  );
}

export default JobCard;
