import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const Home = ({ user }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const res = await axiosInstance.get("/jobs");
      setJobs(res.data);
    };
    fetchJobs();
  }, []);

  const applyJob = async (job) => {
    if (!user) {
      alert("Please login first!");
      return;
    }
    await axiosInstance.post(`/jobs/apply/${job._id}`);
    alert(`${user.name} applied for ${job.title}`);
  };

  return (
    <div style={{ padding: "20px", display: "flex", gap: "20px" }}>
      {jobs.map((job) => (
        <div key={job._id} style={{ border: "1px solid gray", padding: "10px", borderRadius: "5px", width: "200px" }}>
          <b>{job.title}</b>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary}</p>
          <button onClick={() => applyJob(job)}>Apply Job</button>
        </div>
      ))}
    </div>
  );
};

export default Home;
