import React from "react";
import { Link } from "react-router-dom";

const Navbar = ({ user, onLogout }) => {
  return (
    <nav style={{ padding: "10px 20px", background: "#3f51b5", color: "#fff", display: "flex", justifyContent: "space-between" }}>
      <div><Link to="/" style={{ color: "#fff", textDecoration: "none" }}>Job Board</Link></div>
      <div>
        <Link to="/" style={{ marginRight: "15px", color: "#fff", textDecoration: "none" }}>Home</Link>
        {!user && <Link to="/login" style={{ marginRight: "15px", color: "#fff", textDecoration: "none" }}>Login</Link>}
        {!user && <Link to="/register" style={{ marginRight: "15px", color: "#fff", textDecoration: "none" }}>Register</Link>}
        {user && (
          <>
            <span style={{ marginRight: "15px" }}>Hello, {user.name}</span>
            <button onClick={onLogout} style={{ color: "#fff", background: "transparent", border: "none", cursor: "pointer" }}>Logout</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
