import React from "react";
import { useNavigate } from "react-router-dom";

const JobCard = ({ job }) => {
  const navigate = useNavigate();
  return (
    <div className="border p-4 rounded shadow hover:shadow-lg transition cursor-pointer" onClick={() => navigate(`/job/${job._id}`)}>
      <h2 className="font-bold text-lg">{job.title}</h2>
      <p className="text-gray-600">{job.company}</p>
      <p className="mt-2">{job.description.substring(0, 80)}...</p>
      <p className="mt-2 text-sm text-gray-500">Location: {job.location} | Salary: {job.salary}</p>
    </div>
  );
};

export default JobCard;
