import React, { useEffect, useState } from "react";
import axiosInstance from "../axiosInstance";

const Home = ({ user }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const { data } = await axiosInstance.get("/jobs");
      setJobs(data);
    };
    fetchJobs();
  }, []);

  const applyJob = async (job) => {
    if (!user) return alert("You must login to apply!");
    try {
      const { data } = await axiosInstance.post(`/jobs/apply/${job._id}`);
      alert(`${user.name} applied for ${job.title}`);
    } catch (err) {
      alert(err.response?.data?.msg || "Error applying job");
    }
  };

  return (
    <div style={{ padding: "20px", display: "flex", gap: "20px" }}>
      {jobs.map(job => (
        <div key={job._id} style={{ border: "1px solid #ccc", padding: "10px", width: "250px" }}>
          <h3>{job.title}</h3>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary}</p>
          <button onClick={() => applyJob(job)}>Apply Job</button>
        </div>
      ))}
    </div>
  );
};

export default Home;
