import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance";

const JobDetail = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);

  useEffect(() => {
    axiosInstance.get("/jobs")
      .then(res => setJob(res.data.find(j => j._id === id)))
      .catch(err => console.log(err));
  }, [id]);

  const applyJob = () => {
    axiosInstance.post(`/jobs/apply/${id}`)
      .then(res => alert(res.data.msg))
      .catch(err => alert(err.response?.data?.msg || "Error applying"));
  };

  if (!job) return <p className="p-4">Loading...</p>;

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold">{job.title}</h1>
      <p className="text-gray-600">{job.company}</p>
      <p className="mt-2">{job.description}</p>
      <p className="mt-2">Location: {job.location}</p>
      <p className="mt-2">Salary: {job.salary}</p>
      <button className="bg-green-500 text-white px-4 py-2 mt-4 rounded" onClick={applyJob}>
        Apply Job
      </button>
    </div>
  );
};

export default JobDetail;
