import React from "react";
import { Link } from "react-router-dom";

const Navbar = ({ user, setUser }) => {
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <nav style={{ backgroundColor: "#4c7cff", padding: "10px", color: "white", display: "flex", justifyContent: "space-between" }}>
      <div>
        <Link to="/" style={{ color: "white", textDecoration: "none", marginRight: "15px" }}>Home</Link>
      </div>
      <div>
        {user ? (
          <>
            <span style={{ marginRight: "15px" }}>Hello, {user.name}</span>
            <button onClick={handleLogout} style={{ backgroundColor: "transparent", color: "white", border: "none", cursor: "pointer" }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" style={{ color: "white", marginRight: "10px" }}>Login</Link>
            <Link to="/register" style={{ color: "white" }}>Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
