// src/pages/Home.js
import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance"; // your axiosInstance.js
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [user, setUser] = useState(null); // load from localStorage after login

  useEffect(() => {
    fetchJobs();

    // get user from localStorage if logged in
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("/jobs");
      setJobs(res.data);
    } catch (error) {
      console.error(error);
      toast.error("Failed to load jobs");
    }
  };

  const applyJob = async (jobId) => {
    if (!user) {
      toast.error("You must be logged in to apply");
      return;
    }

    try {
      const res = await axios.post(`/jobs/apply/${jobId}`, {}, {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      });
      toast.success(res.data.msg);
    } catch (error) {
      console.error(error);
      toast.error(error.response?.data?.msg || "Failed to apply");
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Job Listings</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {jobs.length === 0 ? (
          <p>No jobs available</p>
        ) : (
          jobs.map((job) => (
            <div key={job._id} className="border p-4 rounded shadow">
              <h2 className="text-xl font-semibold">{job.title}</h2>
              <p className="text-gray-600">{job.company}</p>
              <p className="mt-2">{job.description}</p>
              <p className="mt-1 text-sm text-gray-500">
                Location: {job.location} | Salary: {job.salary || "N/A"}
              </p>
              <button
                onClick={() => applyJob(job._id)}
                className="mt-4 w-full bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded"
              >
                Apply Job
              </button>
            </div>
          ))
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default Home;
