import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance";

const JobDetail = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);
  const token = localStorage.getItem("token");

  const fetchJob = async () => {
    try {
      const res = await axiosInstance.get(`/job/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setJob(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const applyJob = async () => {
    if (!token) return alert("Please login to apply for this job");
    try {
      await axiosInstance.post(`/job/apply/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert(`Application successful for ${job.title}`);
    } catch (err) {
      alert(err.response?.data?.msg || "Failed to apply");
    }
  };

  useEffect(() => {
    fetchJob();
  }, []);

  if (!job) return <p>Loading...</p>;

  return (
    <div className="p-4 max-w-xl mx-auto">
      <div className="bg-white shadow p-4 rounded">
        <h2 className="font-bold text-xl mb-2">{job.title}</h2>
        <p><strong>Company:</strong> {job.company}</p>
        <p><strong>Location:</strong> {job.location}</p>
        <p><strong>Salary:</strong> {job.salary}</p>
        <p><strong>Skills Required:</strong> {job.skills || "N/A"}</p>
        <button onClick={applyJob} className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600">
          Apply Job
        </button>
      </div>
    </div>
  );
};

export default JobDetail;
