import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import { toast } from "react-toastify";

const Home = ({ user }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get("/jobs");
        setJobs(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchJobs();
  }, []);

  const applyJob = async (jobId) => {
    if (!user) {
      toast.error("You must be logged in to apply");
      return;
    }

    try {
      const res = await axios.post(
        `/jobs/apply/${jobId}`,
        {},
        {
          headers: { Authorization: `Bearer ${user.token}` },
        }
      );
      toast.success(res.data.msg);
    } catch (err) {
      toast.error(err.response?.data?.msg || "Error applying job");
    }
  };

  return (
    <div className="p-6 grid grid-cols-1 md:grid-cols-3 gap-4">
      {jobs.length === 0 && <p>No jobs available</p>}
      {jobs.map((job) => (
        <div
          key={job._id}
          className="border p-4 rounded shadow flex flex-col justify-between"
        >
          <div>
            <h2 className="font-bold text-lg">{job.title}</h2>
            <p>{job.company}</p>
            <p>{job.description}</p>
            <p>Location: {job.location} | Salary: {job.salary || "N/A"}</p>
          </div>
          <button
            onClick={() => applyJob(job._id)}
            className="mt-4 bg-green-500 text-white py-2 rounded"
          >
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
