import React, { useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axiosInstance.post("/login", { email, password })
      .then(res => {
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("user", JSON.stringify(res.data.user));
        alert(res.data.msg);
        window.location.href = "/";
      })
      .catch(err => alert(err.response?.data?.msg || "Login failed"));
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="font-bold text-xl mb-4">Login</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <input placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="border p-2 rounded"/>
        <input placeholder="Password" value={password} type="password" onChange={e => setPassword(e.target.value)} className="border p-2 rounded"/>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded mt-2">Login</button>
      </form>
    </div>
  );
};

export default Login;
