// src/components/Navbar.js
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { isLoggedIn, logout, getRole } from "../utils/auth";

const Navbar = () => {
  const navigate = useNavigate();
  const role = getRole();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="bg-blue-500 p-4 text-white flex justify-between">
      <div>
        <Link className="font-bold text-lg" to="/">Job Board</Link>
      </div>
      <div className="space-x-4">
        {!isLoggedIn() && (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
        {isLoggedIn() && (
          <>
            {role === "employer" && <span>Employer</span>}
            {role === "candidate" && <span>Candidate</span>}
            <button onClick={handleLogout} className="bg-red-600 px-2 py-1 rounded">
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
