// src/App.js
import { Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import JobDetail from "./pages/JobDetail";
import CandidateDashboard from "./pages/CandidateDashboard";
import EmployerDashboard from "./pages/EmployerDashboard";
import { useState, useEffect } from "react";

// ProtectedRoute
const ProtectedRoute = ({ user, allowedRole, children }) => {
  if (!user) return <Navigate to="/login" replace />;
  if (allowedRole && user.role !== allowedRole) return <Navigate to="/" replace />;
  return children;
};

function App() {
  const [user, setUser] = useState(null);

  // GLOBAL SEARCH STATE  ✅ REQUIRED FOR HERO + HOME
  const [search, setSearch] = useState("");

  // LOAD USER ON REFRESH
  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedUser = localStorage.getItem("user");
    if (token && storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const logout = () => {
    localStorage.clear();
    setUser(null);
  };

  return (
    <>
      <Navbar user={user} logout={logout} />

      <Routes>
        {/* HOME PAGE + PASS SEARCH STATE */}
        <Route
          path="/"
          element={<Home search={search} setSearch={setSearch} />}
        />

        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/register" element={<Register setUser={setUser} />} />

        {/* JOB DETAIL */}
        <Route path="/job/:id" element={<JobDetail />} />

        {/* CANDIDATE DASHBOARD */}
        <Route
          path="/candidate-dashboard"
          element={
            <ProtectedRoute user={user} allowedRole="candidate">
              <CandidateDashboard />
            </ProtectedRoute>
          }
        />

        {/* EMPLOYER DASHBOARD */}
        <Route
          path="/employer"
          element={
            <ProtectedRoute user={user} allowedRole="employer">
              <EmployerDashboard />
            </ProtectedRoute>
          }
        />
      </Routes>
    </>
  );
}

export default App;
