import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Home = ({ user }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const res = await axios.get("/jobs");
      setJobs(res.data);
    };
    fetchJobs();
  }, []);

  const applyJob = async (jobId, jobTitle) => {
    if (!user) return toast.error("You must be logged in to apply");
    try {
      await axios.post(
        `/jobs/apply/${jobId}`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      toast.success(`${user.name} applied for ${jobTitle}`);
    } catch (err) {
      toast.error(err.response?.data?.msg || "Error applying for job");
    }
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {jobs.map((job) => (
          <div
            key={job._id}
            className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition"
          >
            <h2 className="text-lg font-bold mb-2">{job.title}</h2>
            <p className="text-gray-600 mb-1">{job.company}</p>
            <p className="text-gray-500 mb-1">{job.description}</p>
            <p className="text-gray-500 mb-4">
              Location: {job.location} | Salary: {job.salary}
            </p>
            <button
              onClick={() => applyJob(job._id, job.title)}
              className="w-full bg-green-500 text-white py-2 rounded hover:bg-green-600 transition"
            >
              Apply Job
            </button>
          </div>
        ))}
      </div>
      <ToastContainer position="top-right" autoClose={2500} />
    </div>
  );
};

export default Home;
