// frontend/src/pages/EmployerDashboard.js
import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";
import { Link } from "react-router-dom";

const EmployerDashboard = () => {
  const [title, setTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [salary, setSalary] = useState("");
  const [description, setDescription] = useState("");

  const [myJobs, setMyJobs] = useState([]);
  const [editingJob, setEditingJob] = useState(null);

  const fetchMyJobs = async () => {
    try {
      // There is no dedicated route for "my jobs" in original — fetch all and filter by createdBy on server or assume backend exposes createdBy in jobs
      const res = await axiosInstance.get("/jobs");
      const user = JSON.parse(localStorage.getItem("user") || "null");
      const filtered = res.data.filter(j => j.createdBy === user?.id || j.createdBy?._id === user?.id);
      setMyJobs(filtered);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchMyJobs();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    axiosInstance
      .post("/jobs", { title, company, description, location, salary })
      .then((res) => {
        alert("Job Created Successfully!");
        setMyJobs(prev => [res.data, ...prev]);
        setTitle(""); setCompany(""); setLocation(""); setSalary(""); setDescription("");
      })
      .catch((err) => {
        const msg = err.response?.data?.msg;
        if (msg === "Invalid token" || msg === "Unauthorized") {
          alert("Session expired, please login again.");
          window.location.href = "/login";
        } else {
          alert(msg || "Error creating job");
        }
      });
  };

  const handleDelete = async (jobId) => {
    if (!window.confirm("Delete this job and all related applications?")) return;
    try {
      await axiosInstance.delete(`/jobs/${jobId}`);
      setMyJobs(prev => prev.filter(j => j._id !== jobId));
      alert("Job deleted");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.msg || "Error deleting job");
    }
  };

  const startEdit = (job) => {
    setEditingJob(job);
    setTitle(job.title);
    setCompany(job.company);
    setLocation(job.location);
    setSalary(job.salary || "");
    setDescription(job.description);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await axiosInstance.put(`/jobs/${editingJob._id}`, {
        title, company, description, location, salary
      });
      // refresh list
      setMyJobs(prev => prev.map(j => j._id === res.data.job._id ? res.data.job : j));
      alert("Job updated");
      setEditingJob(null);
      setTitle(""); setCompany(""); setLocation(""); setSalary(""); setDescription("");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.msg || "Error updating job");
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6 mt-10">
      <h2 className="text-3xl font-bold mb-6">Employer Dashboard</h2>

      <div className="bg-white shadow p-6 rounded mb-10">
        <h3 className="text-xl font-bold mb-4">{editingJob ? "Edit Job" : "Post a New Job"}</h3>

        <form onSubmit={editingJob ? handleUpdate : handleSubmit} className="space-y-4">
          <input type="text" placeholder="Job Title" value={title} onChange={(e)=>setTitle(e.target.value)} className="w-full p-3 border rounded" required />
          <input type="text" placeholder="Company Name" value={company} onChange={(e)=>setCompany(e.target.value)} className="w-full p-3 border rounded" required />
          <input type="text" placeholder="Job Location" value={location} onChange={(e)=>setLocation(e.target.value)} className="w-full p-3 border rounded" required />
          <input type="text" placeholder="Salary (optional)" value={salary} onChange={(e)=>setSalary(e.target.value)} className="w-full p-3 border rounded" />
          <textarea placeholder="Job Description" value={description} onChange={(e)=>setDescription(e.target.value)} className="w-full p-3 border rounded" rows="4" required></textarea>

          <div className="flex gap-2">
            <button type="submit" className="bg-blue-600 text-white py-2 px-4 rounded">
              {editingJob ? "Save Changes" : "Create Job"}
            </button>

            {editingJob && (
              <button type="button" onClick={() => {
                setEditingJob(null);
                setTitle(""); setCompany(""); setLocation(""); setSalary(""); setDescription("");
              }} className="bg-gray-300 px-4 py-2 rounded">Cancel</button>
            )}
          </div>
        </form>
      </div>

      <div>
        <h3 className="text-2xl font-bold mb-4">My Posted Jobs</h3>

        {myJobs.length === 0 ? (
          <p>No jobs posted yet.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {myJobs.map((job) => (
              <div key={job._id} className="bg-white shadow p-4 rounded border">
                <h4 className="text-xl font-bold">{job.title}</h4>
                <p className="text-gray-700">{job.company}</p>
                <p className="text-gray-700">{job.location}</p>

                <div className="mt-3 flex gap-2">
                  <Link to={`/job/${job._id}`} className="bg-blue-600 text-white py-1 px-3 rounded">View Job</Link>

                  <Link to={`/employer/jobs/${job._id}/applicants`} className="bg-indigo-600 text-white py-1 px-3 rounded">View Applicants</Link>

                  <button onClick={() => startEdit(job)} className="bg-yellow-400 text-black py-1 px-3 rounded">Edit</button>

                  <button onClick={() => handleDelete(job._id)} className="bg-red-500 text-white py-1 px-3 rounded">Delete</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default EmployerDashboard;
