import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";
import { Link } from "react-router-dom";

const EmployerDashboard = () => {
  const [title, setTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [salary, setSalary] = useState("");
  const [description, setDescription] = useState("");

  const [myJobs, setMyJobs] = useState([]);

  // Load employer's own jobs
  useEffect(() => {
    axiosInstance
      .get("/jobs/employer/myjobs")
      .then((res) => setMyJobs(res.data))
      .catch((err) => console.log(err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    axiosInstance
      .post("/jobs", {
        title,
        company,
        description,
        location,
        salary,
      })
      .then((res) => {
        alert("Job Created Successfully!");

        // Refresh jobs list instantly
        setMyJobs((prev) => [...prev, res.data]);

        // Clear form
        setTitle("");
        setCompany("");
        setLocation("");
        setSalary("");
        setDescription("");
      })
      .catch((err) => {
        const msg = err.response?.data?.msg;
        if (msg === "Invalid token" || msg === "Unauthorized") {
          alert("Session expired, please login again.");
          window.location.href = "/login";
        } else {
          alert(msg || "Error creating job");
        }
      });
  };

  return (
    <div className="max-w-5xl mx-auto p-6 mt-10">
      <h2 className="text-3xl font-bold mb-6">Employer Dashboard</h2>

      {/* POST JOB FORM */}
      <div className="bg-white shadow p-6 rounded mb-10">
        <h3 className="text-xl font-bold mb-4">Post a New Job</h3>

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            placeholder="Job Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full p-3 border rounded"
            required
          />

          <input
            type="text"
            placeholder="Company Name"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
            className="w-full p-3 border rounded"
            required
          />

          <input
            type="text"
            placeholder="Job Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="w-full p-3 border rounded"
            required
          />

          <input
            type="text"
            placeholder="Salary (optional)"
            value={salary}
            onChange={(e) => setSalary(e.target.value)}
            className="w-full p-3 border rounded"
          />

          <textarea
            placeholder="Job Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full p-3 border rounded"
            rows="4"
            required
          ></textarea>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
          >
            Create Job
          </button>
        </form>
      </div>

      {/* MY JOBS LIST */}
      <div>
        <h3 className="text-2xl font-bold mb-4">My Posted Jobs</h3>

        {myJobs.length === 0 ? (
          <p>No jobs posted yet.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {myJobs.map((job) => (
              <div
                key={job._id}
                className="bg-white shadow p-4 rounded border"
              >
                <h4 className="text-xl font-bold">{job.title}</h4>
                <p className="text-gray-700">{job.company}</p>
                <p className="text-gray-700">{job.location}</p>

                <Link
                  to={`/job/${job._id}`}
                  className="block bg-blue-600 text-white text-center py-2 mt-3 rounded hover:bg-blue-700"
                >
                  View Job
                </Link>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default EmployerDashboard;
