import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "../axiosInstance";

const Register = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ name: "", email: "", password: "", role: "Candidate" });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axiosInstance.post("/register", form);
      alert("Registered successfully!");
      navigate("/login");
    } catch (err) {
      alert(err.response.data.msg);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Name" name="name" value={form.name} onChange={handleChange} required /><br /><br />
        <input placeholder="Email" name="email" value={form.email} onChange={handleChange} required /><br /><br />
        <input placeholder="Password" name="password" type="password" value={form.password} onChange={handleChange} required /><br /><br />
        <select name="role" value={form.role} onChange={handleChange}>
          <option value="Candidate">Candidate</option>
          <option value="Recruiter">Recruiter</option>
        </select><br /><br />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
