import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance";

const CandidateDashboard = () => {
  const [appliedJobs, setAppliedJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axiosInstance
      .get("/jobs/my-applications")
      .then((res) => {
        setAppliedJobs(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p className="text-center mt-10">Loading applications...</p>;
  }

  return (
    <div className="max-w-5xl mx-auto my-8 p-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">My Applications</h1>

      {appliedJobs.length === 0 ? (
        <p className="text-gray-600">You haven't applied to any jobs yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {appliedJobs.map((job) => (
            <div
              key={job._id}
              className="border rounded-lg p-4 shadow hover:shadow-lg transition bg-white"
            >
              <h2 className="text-xl font-bold mb-2">{job.title}</h2>

              <p className="text-gray-700">
                <strong>Company:</strong> {job.company}
              </p>

              <p className="text-gray-700">
                <strong>Location:</strong> {job.location}
              </p>

              <p className="text-gray-700 mb-2">
                <strong>Salary:</strong> {job.salary || "N/A"}
              </p>

              <p className="text-green-600 font-semibold mb-3">
                Status: Applied
              </p>

              {/* NEW: view job button */}
              <Link
                to={`/job/${job._id}`}
                className="block bg-blue-600 text-white text-center py-2 rounded hover:bg-blue-700 transition"
              >
                View Job
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CandidateDashboard;
