import React from "react";
import { Link } from "react-router-dom";

export default function JobCard({ job }) {
  return (
    <div className="bg-white shadow-md p-4 rounded w-full">
      <h2 className="font-bold text-lg">{job.title}</h2>
      <p className="text-gray-600">{job.company}</p>
      <p className="text-gray-600">{job.description?.substring(0, 60)}...</p>
      <p className="text-gray-600">Location: {job.location} | Salary: {job.salary}</p>
      <Link 
        to={`/job/${job._id}`}
        className="mt-2 inline-block bg-blue-500 text-white w-full text-center py-2 rounded hover:bg-blue-600"
      >
        Apply Job
      </Link>
    </div>
  );
}
