import React from "react";
import { Link } from "react-router-dom";

export default function Navbar({ user, logout }) {
  return (
    <nav className="bg-blue-600 p-4 flex justify-between items-center text-white">
      <Link to="/" className="font-bold text-xl">JobBoard</Link>
      <div>
        {user ? (
          <>
            <span className="mr-4">Hello, {user.name}</span>
            <Link to={user.role === "employer" ? "/employer" : "/candidate"} className="mr-4">Profile</Link>
            <button onClick={logout} className="bg-red-500 px-4 py-1 rounded">Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" className="mr-4">Login</Link>
            <Link to="/register" className="bg-green-500 px-4 py-1 rounded">Register</Link>
          </>
        )}
      </div>
    </nav>
  );
}
