import { useState, useEffect } from "react";
import axios from "../utils/axiosInstance";

export default function EmployerDashboard({ user }) {
  const [title, setTitle] = useState("");
  const [company, setCompany] = useState("");
  const [description, setDescription] = useState("");
  const [location, setLocation] = useState("");
  const [salary, setSalary] = useState("");
  const [jobs, setJobs] = useState([]);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("/job");
      setJobs(res.data.filter(j => j.postedBy === user._id));
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => { fetchJobs(); }, []);

  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/job/create", { title, company, description, location, salary });
      alert("Job created");
      fetchJobs();
      setTitle(""); setCompany(""); setDescription(""); setLocation(""); setSalary("");
    } catch (err) {
      alert(err.response?.data?.msg || "Error creating job");
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Employer Dashboard</h2>
      <form onSubmit={handleCreate} className="bg-white p-4 rounded shadow mb-6">
        <input placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} className="w-full mb-2 p-2 border rounded" required/>
        <input placeholder="Company" value={company} onChange={e => setCompany(e.target.value)} className="w-full mb-2 p-2 border rounded" required/>
        <input placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} className="w-full mb-2 p-2 border rounded" required/>
        <input placeholder="Location" value={location} onChange={e => setLocation(e.target.value)} className="w-full mb-2 p-2 border rounded" required/>
        <input placeholder="Salary" value={salary} onChange={e => setSalary(e.target.value)} className="w-full mb-2 p-2 border rounded" required/>
        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">Create Job</button>
      </form>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {jobs.map(j => (
          <div key={j._id} className="p-4 bg-white rounded shadow">
            <h3 className="font-bold">{j.title}</h3>
            <p>{j.company}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
