import React, { useState } from "react";
import axiosInstance from "../axiosInstance";
import { useNavigate } from "react-router-dom";

const Register = ({ onRegister }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("Candidate");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axiosInstance.post("/register", { name, email, password, role });
      onRegister(res.data.user);
      localStorage.setItem("token", btoa(JSON.stringify({ id: res.data.user.id, email: res.data.user.email, role: res.data.user.role })));
      navigate("/");
    } catch (error) {
      alert(error.response?.data?.msg || "Registration failed");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: "400px", margin: "50px auto" }}>
      <h2>Register</h2>
      <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} required style={{ display: "block", width: "100%", margin: "10px 0", padding: "10px" }} />
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required style={{ display: "block", width: "100%", margin: "10px 0", padding: "10px" }} />
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required style={{ display: "block", width: "100%", margin: "10px 0", padding: "10px" }} />
      <select value={role} onChange={e => setRole(e.target.value)} style={{ display: "block", width: "100%", margin: "10px 0", padding: "10px" }}>
        <option value="Candidate">Candidate</option>
        <option value="Employer">Employer</option>
      </select>
      <button type="submit" style={{ padding: "10px 20px", background: "#3f51b5", color: "#fff", border: "none", cursor: "pointer" }}>Register</button>
    </form>
  );
};

export default Register;
