import { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";

export default function CandidateDashboard({ user }) {
  const [appliedJobs, setAppliedJobs] = useState([]);

  useEffect(() => {
    if (!user) return;

    const fetchAppliedJobs = async () => {
      try {
        const res = await axios.get(`/job/applied/${user._id}`);
        setAppliedJobs(res.data); // Array of job IDs
      } catch (err) {
        console.log(err);
      }
    };

    fetchAppliedJobs();
  }, [user]);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-bold mb-4">My Applied Jobs</h2>
      {appliedJobs.length === 0 ? (
        <p>You havenâ€™t applied to any jobs yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {appliedJobs.map((jobId) => (
            <AppliedJobCard key={jobId} jobId={jobId} />
          ))}
        </div>
      )}
    </div>
  );
}

function AppliedJobCard({ jobId }) {
  const [job, setJob] = useState(null);

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await axios.get(`/job/${jobId}`);
        setJob(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchJob();
  }, [jobId]);

  if (!job) return <div className="p-4 bg-white rounded shadow">Loading...</div>;

  return (
    <div className="p-4 bg-white rounded shadow">
      <h3 className="font-bold text-lg">{job.title}</h3>
      <p>{job.company}</p>
      <p>{job.location}</p>
      <p>Salary: {job.salary}</p>
    </div>
  );
}
