import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

const Home = ({ user }) => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const res = await axiosInstance.get("/jobs");
      setJobs(res.data);
    };
    fetchJobs();
  }, []);

  const applyJob = async (jobId, jobTitle) => {
    if (!user) return alert("You must be logged in to apply");
    try {
      await axiosInstance.post(`/jobs/apply/${jobId}`);
      alert(`${user.name} applied for ${jobTitle}`);
    } catch (err) {
      alert(err.response?.data?.msg || "Apply failed");
    }
  };

  return (
    <div style={{ display: "flex", justifyContent: "space-around", marginTop: "30px" }}>
      {jobs.map((job) => (
        <div key={job._id} style={{ border: "1px solid #ccc", padding: "15px", width: "300px" }}>
          <h3>{job.title}</h3>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary}</p>
          <button onClick={() => applyJob(job._id, job.title)}>Apply Job</button>
        </div>
      ))}
    </div>
  );
};

export default Home;
