import React, { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

export default function Home() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axiosInstance.get("/jobs")
      .then(res => {
        setJobs(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p className="p-4">Loading jobs...</p>;
  if (!jobs.length) return <p className="p-4">No jobs available yet.</p>;

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {jobs.map(job => (
        <div key={job._id} className="border p-4 rounded shadow">
          <h2 className="font-bold">{job.title}</h2>
          <p>{job.company}</p>
          <p>{job.description}</p>
          <p>Location: {job.location} | Salary: {job.salary || "N/A"}</p>
          <button
            className="bg-green-500 text-white px-4 py-2 mt-2 rounded"
            onClick={() => alert("Apply Job clicked")}
          >
            Apply Job
          </button>
        </div>
      ))}
    </div>
  );
}
