import { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

export default function CandidateDashboard({ user }) {
  const [appliedJobs, setAppliedJobs] = useState([]);

  useEffect(() => {
    if (!user) return;
    axiosInstance.get(`/job/applied/${user.userId}`).then(res => setAppliedJobs(res.data)).catch(console.error);
  }, [user]);

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Jobs You Applied</h2>
      {appliedJobs.length === 0 && <p>No applications yet</p>}
      <ul className="space-y-2">
        {appliedJobs.map(job => (
          <li key={job._id} className="border p-2 rounded shadow">{job.title}</li>
        ))}
      </ul>
    </div>
  );
}
