import { useEffect, useState } from "react";
import axiosInstance from "../utils/axiosInstance";

export default function EmployerDashboard({ user }) {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    if (!user) return;
    axiosInstance.get("/job").then(res => {
      const myJobs = res.data.filter(job => job.postedBy === user.userId);
      setJobs(myJobs);
    }).catch(console.error);
  }, [user]);

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Your Posted Jobs</h2>
      {jobs.length === 0 && <p>No jobs posted yet</p>}
      <ul className="space-y-2">
        {jobs.map(job => (
          <li key={job._id} className="border p-2 rounded shadow">
            {job.title} | {job.location} | Salary: {job.salary}
          </li>
        ))}
      </ul>
    </div>
  );
}
