import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance";

const JobDetail = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [alreadyApplied, setAlreadyApplied] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Load logged-in user
    const storedUser = localStorage.getItem("user");
    if (storedUser) setUser(JSON.parse(storedUser));

    // Fetch job details
    axiosInstance
      .get(`/jobs/${id}`)
      .then((res) => {
        setJob(res.data);

        // Check if user has applied
        const tokenUser = JSON.parse(localStorage.getItem("user") || "{}");
        if (res.data.applicants?.includes(tokenUser.id)) {
          setAlreadyApplied(true);
        }

        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  }, [id]);

  const applyJob = async () => {
    if (!user) {
      alert("Please login to apply for this job.");
      return;
    }

    if (user.role === "employer") {
      alert("Employers cannot apply for jobs.");
      return;
    }

    try {
      const res = await axiosInstance.post(`/jobs/apply/${id}`);
      alert(res.data.msg);
      setAlreadyApplied(true);
    } catch (err) {
      alert(err.response?.data?.msg || "Error applying");
    }
  };

  if (loading)
    return <p className="text-center mt-10">Loading job details...</p>;

  if (!job)
    return <p className="text-center mt-10 text-red-500">Job not found.</p>;

  return (
    <div className="max-w-4xl mx-auto my-8 p-6 bg-white rounded shadow">
      <Link
        to="/"
        className="text-blue-600 underline mb-4 inline-block"
      >
        ‚Üê Back to Jobs
      </Link>

      <h1 className="text-3xl font-bold mb-4">{job.title}</h1>

      <p className="text-gray-700 mb-2">
        <strong>Company:</strong> {job.company}
      </p>

      <p className="text-gray-700 mb-2">
        <strong>Location:</strong> {job.location}
      </p>

      <p className="text-gray-700 mb-4">
        <strong>Salary:</strong> {job.salary || "N/A"}
      </p>

      <p className="text-gray-700 whitespace-pre-line mb-6 leading-relaxed">
        {job.description}
      </p>

      {/* APPLY BUTTON CONDITIONS */}
      {!user && (
        <button
          onClick={() => alert("Please login to apply")}
          className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-500 transition"
        >
          Login to Apply
        </button>
      )}

      {user && user.role === "employer" && (
        <p className="text-red-600 font-semibold">
          Employers cannot apply for jobs.
        </p>
      )}

      {user && user.role === "candidate" && alreadyApplied && (
        <button
          disabled
          className="bg-gray-400 text-white px-6 py-3 rounded cursor-not-allowed"
        >
          Already Applied
        </button>
      )}

      {user && user.role === "candidate" && !alreadyApplied && (
        <button
          onClick={applyJob}
          className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-500 transition"
        >
          Apply Job
        </button>
      )}
    </div>
  );
};

export default JobDetail;
