import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ user, setUser }) => {
  const navigate = useNavigate();

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <nav style={{ padding: "10px", backgroundColor: "#3b82f6", color: "white", display: "flex", justifyContent: "space-between" }}>
      <div><Link to="/" style={{ color: "white", textDecoration: "none" }}>Home</Link></div>
      <div>
        {user ? (
          <>
            Hello, {user.name} <span onClick={logout} style={{ cursor: "pointer", textDecoration: "underline", marginLeft: "10px" }}>Logout</span>
          </>
        ) : (
          <>
            <Link to="/login" style={{ color: "white", marginRight: "10px" }}>Login</Link>
            <Link to="/register" style={{ color: "white" }}>Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
