import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import { getUser } from "../utils/auth";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [appliedJobs, setAppliedJobs] = useState([]);

  const user = getUser(); // get current logged in user

  useEffect(() => {
    fetchJobs();
    if (user) fetchAppliedJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const response = await axios.get("/jobs"); // GET /api/jobs
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching jobs", error);
    }
  };

  const fetchAppliedJobs = async () => {
    try {
      const response = await axios.get(`/applications/${user.id}`); // GET /api/applications/:userId
      setAppliedJobs(response.data.map((job) => job.jobId));
    } catch (error) {
      console.error("Error fetching applied jobs", error);
    }
  };

  const applyJob = async (jobId) => {
    try {
      await axios.post("/applications", {
        userId: user.id,
        jobId: jobId,
      });
      setAppliedJobs([...appliedJobs, jobId]); // mark as applied
      alert("Applied successfully!");
    } catch (error) {
      console.error("Error applying job", error);
      alert("Failed to apply. Try again.");
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Job Listings</h1>
      {jobs.map((job) => (
        <div
          key={job.id}
          className="border p-4 rounded mb-4 flex justify-between items-center"
        >
          <div>
            <h2 className="text-xl font-semibold">{job.title}</h2>
            <p>{job.company}</p>
            <p>{job.location}</p>
          </div>
          {user && user.role === "candidate" && (
            <button
              className={`px-4 py-2 rounded ${
                appliedJobs.includes(job.id)
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-green-500 hover:bg-green-600 text-white"
              }`}
              disabled={appliedJobs.includes(job.id)}
              onClick={() => applyJob(job.id)}
            >
              {appliedJobs.includes(job.id) ? "Applied" : "Apply"}
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default Home;
