import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";
import Hero from "../components/Hero";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [search, setSearch] = useState("");
  const [filteredJobs, setFilteredJobs] = useState([]);

  useEffect(() => {
    axiosInstance.get("/jobs")
      .then(res => {
        setJobs(res.data);
        setFilteredJobs(res.data);
      })
      .catch(err => console.log(err));
  }, []);

  const handleSearch = (e) => {
    const value = e.target.value;
    setSearch(value);
    const filtered = jobs.filter(
      job =>
        job.title.toLowerCase().includes(value.toLowerCase()) ||
        job.company.toLowerCase().includes(value.toLowerCase()) ||
        job.location.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredJobs(filtered);
  };

  const applyJob = (jobId) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login to apply for jobs");
      return;
    }

    axiosInstance.post(`/jobs/apply/${jobId}`)
      .then(res => alert(res.data.msg))
      .catch(err => alert(err.response?.data?.msg || "Error applying"));
  };

  return (
    <div className="p-4">
      <Hero />

      {/* Search Bar */}
      <div className="max-w-4xl mx-auto my-6">
        <input
          type="text"
          value={search}
          onChange={handleSearch}
          placeholder="Search by job title, company, or location"
          className="w-full p-3 rounded shadow focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
      </div>

      {/* Job Listings Grid */}
      <div id="jobs" className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {filteredJobs.map(job => (
          <div key={job._id} className="border rounded-lg p-4 shadow hover:shadow-lg transition bg-white">
            <h2 className="text-xl font-bold mb-2">{job.title}</h2>
            <p className="text-gray-600 mb-1"><strong>Company:</strong> {job.company}</p>
            <p className="text-gray-600 mb-1"><strong>Location:</strong> {job.location}</p>
            <p className="text-gray-600 mb-1"><strong>Salary:</strong> {job.salary || "N/A"}</p>
            <p className="text-gray-700 mb-3">{job.description}</p>
            <button
              onClick={() => applyJob(job._id)}
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-500 transition"
            >
              Apply Job
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
