import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ user, setUser }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <nav style={{ padding: "10px", background: "#3366FF", color: "#fff", display: "flex", justifyContent: "space-between" }}>
      <div><Link to="/" style={{ color: "#fff", textDecoration: "none", fontWeight: "bold" }}>Job Board</Link></div>
      <div>
        <Link to="/" style={{ marginRight: "10px", color: "#fff" }}>Home</Link>
        {user ? (
          <>
            <span style={{ marginRight: "10px" }}>Hello, {user.name}</span>
            <button onClick={handleLogout} style={{ cursor: "pointer" }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" style={{ marginRight: "10px", color: "#fff" }}>Login</Link>
            <Link to="/register" style={{ color: "#fff" }}>Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
